import SliderItem from "./SliderItem.vue";
export default {
  components: {
    SliderItem,
  },
  props: {
    menuCollapse: {
      type: Boolean,
      default: false,
    },
  },
  computed: {
    slider() {
      return this.$router.options.routes;
    },
  },
  methods: {
    show(item) {
      if (item.meta === undefined || item.meta.notLoad === undefined) {
        return true;
      }
      return !item.meta.notLoad;
    },
  },
};
